import java.util.*;
public class Switchindia {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
Scanner sc=new Scanner(System.in);
System.out.println("enter the name");
String a=sc.nextLine();
switch(a)
{
case "ajay" :System.out.println("ajay leaves in bglor");break;
case "ankit" :System.out.println("ankit leaves in hyderabad");break;
case "arjun" :System.out.println("arjun leaves in hubli");break;
case "akash" :System.out.println("akash leaves in mumbai");break;
default :System.out.println(a+"india");
}
	}

}
